mapper.select:
        select
        ,brand_name,r.NAME as city_name
		from (
			select tabl1.ID, GROUP_CONCAT(tabl1.BRAND_NAME)as brand_name from (
				select d.ID,dealers_car.BRAND_NAME 
				from  dealers_info d left join dealers_car on dealers_car.USER_ID=d.ID
				where dealers_car.BRAND_NAME is not null group by d.ID,dealers_car.BRAND_NAME
				union
select d.id, dealers_car.BRAND_NAME  from dealers_info d left join dealers_car on d.ID=dealers_car.USER_ID and dealers_car.BRAND_NAME is null
			)tabl1 group by ID
	    )tabl2 left join dealers_info on tabl2.ID=dealers_info.ID left join city r on dealers_info.CITY_ID=r.ID

       order by dealers_info.BEGIN_TIME desc
        limit #{offset,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}
